import {Component} from 'react'
import Cookies from 'js-cookie'
// import Header from '../Header'

const trendingApiStatusConstants = {
  initial: 'INITIAL',
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}

// const TopRatedApiStatusConstants = {
//   initial: 'INITIAL',
//   success: 'SUCCESS',
//   failure: 'FAILURE',
//   inProgress: 'IN_PROGRESS',
// }

// const popularApiStatusConstants = {
//   initial: 'INITIAL',
//   success: 'SUCCESS',
//   failure: 'FAILURE',
//   inProgress: 'IN_PROGRESS',
// }

class Home extends Component {
  state = {
    trendingApiStatus: trendingApiStatusConstants.inProgress,
    // topRatedApiStatus: topRatedApiStatusConstants.inProgress,
    // popularApiStatus: popularApiStatusConstants.inProgress,
    trendingMoviesList: [],
    // popularMoviesList: [],
    // trendingMoviesList: [],
  }

  componentDidMount() {
    this.getTrendingMovies()
  }

  getTrendingMovies = async () => {
    this.setState({trendingApiStatus: trendingApiStatusConstants.initial})

    const jwtToken = Cookies.get('jwt_token')
    const url = 'https://apis.ccbp.in/movies-app/trending-movies'
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    }
    const response = await fetch(url, options)
    if (response.ok === true) {
      const data = await response.json()
      console.log(data)
      const updatedData = data.results.map(eachMovie => ({
        backdropPath: eachMovie.backdrop_path,
        id: eachMovie.id,
        overview: eachMovie.overview,
        posterPath: eachMovie.poster_path,
        title: eachMovie.title,
      }))

      this.setState({
        trendingMoviesList: updatedData,
        trendingApiStatus: trendingApiStatusConstants.success,
      })
    }
  }

  render() {
    return <h1>Hello</h1>
  }
}

export default Home
